import { Link } from 'react-router-dom';

class Preferencia extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      nome: this.props.location.state.nome,
      preferencia: '',
    }

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target }) {
    console.log(target.name)
  }

  render() {
    const { nome, preferencia } = this.state;
    return (
      <section>
        <h1>Escolha o certo:</h1>
        <label>
          Biscoito
          <input type="radio" value="biscoito" name="biscoito" onChange={ this.handleChange } />
