import React from 'react';
import { Link } from 'react-router-dom';

class Comida extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      nome: this.props.location.state.nome,
      preferencia: this.props.location.state.preferencia,
      comida: '',
    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick({ target }) {
    this.setState({
      comida: target.name,
    })
  }

  render() {
    const { nome, preferencia, comida } = this.state;
    return (
      <section>
        <h1>Qual é o pior: </h1>
        <button type="button" onClick={ this.handleClick } name="açaí">Açaí</button>
        <button type="button" onClick={ this.handleClick } name="sopa">Sopa</button>
        <button type="button" onClick={ this.handleClick } name="sushi">Sushi</button>
        <Link to={{ pathname:"/animal", state: { nome, preferencia, comida } }}>PRÓXIMO</Link>
      </section>
    );
  }
}

export default Comida;
