import React from 'react';

import './manchete.css';

class Manchete extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      nome: this.props.location.state.nome,
      preferencia: this.props.location.state.preferencia,
      comida: this.props.location.state.comida,
      animal: this.props.location.state.animal,
    }
  }

  render() {
    const { nome, preferencia, comida, animal } = this.state;
    return (
      <section>
        { comida === 'sopa'
        ? <p className="manchete">Em entrevista internacional, 
          {nome} teve que debater sobre a discussão mais acirrada do século, é biscoito ou bolacha? 
          {nome} foi direto na canela dizendo que o certo é {preferencia}! E ainda complementou que sopa não é janta 
          e que nem seu {animal} come!</p>
        : <p className="manchete">Em entrevista internacional, 
          <b>{nome}</b> teve que debater sobre a discussão mais acirrada do século, é biscoito ou bolacha? 
          {nome} foi direto na canela dizendo que o certo é {preferencia}! E ainda complementou que {comida} é ruim 
          e que nem seu {animal} come!</p>
      }
      </section>
    );
  }
}

export default Manchete;
