import React from 'react';
import { Link } from 'react-router-dom';

class Comida extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      comida: '',
    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick({ target }) {
    this.setState({
      comida: target.value,
    })
  }

  render() {
    return (
      <section>
        <h1>Qual é o pior: </h1>
        <button type="button" onClick={ this.handleClick }>Açaí</button>
        <button type="button" onClick={ this.handleClick }>Sopa</button>
        <button type="button" onClick={ this.handleClick }>Sushi</button>
      </section>
    );
  }
}

export default Comida;
